    return callApi('PATCH', `/apps/${APP_IDS.WERKZEUGAUSGABE}/records/${id}`, { fields });
  }
  static async deleteWerkzeugausgabeEntry(id: string) {
    return callApi('DELETE', `/apps/${APP_IDS.WERKZEUGAUSGABE}/records/${id}`);
  }

